import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';

const UserForm = (props) => {


    const { uid } = useParams();
    const navigate = useNavigate();


    const [firstName, setFirstName] = useState(props.userInitialData.first_name);
    const [lastName, setLastName] = useState(props.userInitialData.last_name);
    const [age, setAge] = useState(props.userInitialData.age);
    const [phone, setPhone] = useState(props.userInitialData.phone);

    const [validation, setValidation] = useState(
        {
        isOnChangEventFirstName: false,
        isOnChangEventLastName: false,
        isOnChangEventPhone: false,
        }
    );


    const [options, setOptions] = useState([]);



    useEffect(() => {
        let arr = [];
        for (var i = 0; i < 121; i++) {
            arr.push(i)
        }
        setOptions(arr)

    }, [])




    const submitHandler = (event) => {
        event.preventDefault();
        console.log('submit handler');
        //rember to envoke function.
        if(phone.length<9||isNaN(phone)){
            console.log(isNaN(phone));

        }
        // if(phone.length<9
        //     ||isNaN(phone)
        //     ||firstName.length<3
        //     ||lastName.length<3){
        //         setValidation(    {
        //             isOnChangEventFirstName: true,
        //             isOnChangEventLastName: true,
        //             isOnChangEventPhone: true,
        //             });
        //         console.log(firstName.length);
        //         console.log(lastName.length);
        //         console.log(phone.length);
        //         console.log(isNaN(phone));
        //     }


        const user = {
            first_name: firstName,
            last_name: lastName,
            age: age,
            phone: phone
        }
        if (uid) {
            user.id = uid;
        }

       // props.onSubmitUser(user);

    }

    const handlePhone = (e) => {
        e.preventDefault();
        setValidation({...false, isOnChangEventPhone: true });
        setPhone(e.target.value);

    }

    const handleFirstName = (e) => {
        e.preventDefault();
        setValidation({...false, isOnChangEventFirstName: true });
        setFirstName(e.target.value)

    }
    const handleLastName = (e) => {
        e.preventDefault();
        setLastName(e.target.value)
        setValidation({ ...false, isOnChangEventLastName: true });
        //console.log(validation);
    }

    const handleAge = (e) => {
        e.preventDefault();
        setAge(e.target.value);


    }

    const goToHome = () => {
        navigate('/');
    }


    return (

        <form onSubmit={submitHandler} >



            <input type="text" required id="first-name"
                onChange={handleFirstName} value={firstName} />

            {validation.isOnChangEventFirstName && firstName.length < 3 ? <div>Filed must have at list 3  charters</div> : ''}

            <br />
            <input type="text" required id="last-name"
                onChange={handleLastName} value={lastName} />

            {validation.isOnChangEventLastName && lastName.length < 3 ? <div>Filed must have at list 3  charters</div> : ''}

            <br />
            <select
                value={age}
                onChange={handleAge}
            >
                {
                    options.map((i) => {
                        return <option key={i} value={i}>{i}</option>
                    })
                }

            </select>

            <br />
            <input type="tel" id="phone" name="phone" required
                onChange={handlePhone}
                value={phone}
            />

            <br />
            {validation.isOnChangEventPhone && phone.length < 9  ? <div>Filed must have at list 10  charters</div> : ''}
            {validation.isOnChangEventPhone && isNaN(phone) ? <div>Filed must have only numbers</div> : ''}
            <button>{props.butttoTitle}</button>
        
            <button onClick={goToHome}>Back</button>


        </form>


    )
}

export default UserForm
