import React ,{ useState, useEffect } from 'react'
import UseFetch from '../hooks/UseFetch';
import { useParams } from 'react-router-dom';
import UserForm from '../components/users/UserForm';

const EditUser = () => {

    const [usersfromapi, setUsers] = useState([])
    const {loading , users} =UseFetch();
    const { id } = useParams()
    useEffect(() => {
      
      setUsers(users);
    }, [])

    const singleuser = users.find(u => u.id === id)
    console.log(singleuser)

    
    const userInitialData={
        first_name:singleuser.first_name,
        last_name:singleuser.last_name,
        age:singleuser.age,
        phone:singleuser.phone
    }


    if (loading) {
      return <section>
        <p>Loading...</p>
      </section>
    }
      

    return (
        <>
            <h1>Update User!!!</h1>
            <h2>{singleuser.first_name} {singleuser.last_name}</h2>
            <UserForm userInitialData={userInitialData}/>
        </>
    )
}

export default EditUser
