import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import Input from './Input';
import Select from './Select';
import Button from './Button';

import Card from '../ui/Card';
import classes from './userform.module.css'








const UserForm = (props) => {


    const { uid } = useParams();
    const navigate = useNavigate();

    //user object
    const [user, setUser] = useState({
        firstName:props.userInitialData.first_name,
        lastName:props.userInitialData.last_name,
        age:props.userInitialData.age,
        phone:props.userInitialData.phone
    });


    const [currentChange, setCurrentChange] = useState(


        [
            { isOnChangEventFirstName: false,isValid:false},
            { isOnChangEventLastName: false,isValid:false},
            { isOnChangEventLastName: false,isValid:false},
            { isOnChangEventAge: false,isValid:false},
        ]
    );



    const vlidateUser=(inputChanged)=>{

        if(inputChanged){
            if (user.phone.length < 9 || isNaN(user.phone) || user.firstName.length < 3 || user.lastName.length < 3) {

                
            }
        }

    } 


    const handleDelete = (event) => {
        event.preventDefault();

        if (uid) {

            props.onDeleteUser(uid);
        }

    }


    const submitHandler = (event) => {
        event.preventDefault();
   


        if (user.phone.length < 9 || isNaN(user.phone) || user.firstName.length < 3 || user.lastName.length < 3) {

            setCurrentChange({
                isOnChangEventFirstName: true,
                isOnChangEventLastName: true,
                isOnChangEventPhone: true,
                isOnChangEventAge: true,
            });
            return;
        }




        const Finaluser = {
            first_name: user.firstName,
            last_name: user.lastName,
            age:  user.age,
            phone: user.phone
        }
       
        if (uid) {
            Finaluser.id = uid;
        }

        props.onSubmitUser(Finaluser);

    }


    const HandleInput = (e, name) => {
        e.preventDefault();
 
        switch (name) {
            case 'first_name':
                setUser({...user,firstName:e.target.value})
                setCurrentChange({ ...false, isOnChangEventFirstName: true });
                break;
            case 'last_name':
                setUser({...user,lastName:e.target.value})
                setCurrentChange({ ...false, isOnChangEventLastName: true });
              break;
            case 'age':
                setUser({...user,age:e.target.value})
                setCurrentChange({ ...false, isOnChangEventAge: true });
                break;
            case 'phone':
                setUser({...user,phone:e.target.value})
                setCurrentChange({ ...false, isOnChangEventPhone: true });
                break;

            default:
                break;
        }

  

    }



    const goToHome = () => {
        navigate('/');
    }



    return (
        <Card>

            {props.fname ?
                <div className={classes.user_info_wrap}>

                    <div className={classes.icon_wrap}>
                        <img src="https://avatars.dicebear.com/api/human/:seed.svg" alt="" />
                    </div>

                    <div className={classes.user_info}>
                        <span>{props.fname} {props.lname}</span>
                    </div>

                </div>
                : null
            }

            <form className={classes.form}>

                <Input HandleInputEvent={HandleInput}
                    val={user.firstName}
                    placeholder="First Name"
                    type="Text"
                    name="first_name"
                    isValid={false}
                    validationName="Filed must have at list 3 Charters"
                />

                <Input HandleInputEvent={HandleInput}
                    val={user.lastName}
                    placeholder="Last Name"
                    type="Text"
                    name="last_name"
                    isValid={false}
                    validationName="Filed must have at list 3 Charters"
                />

                <Select
                    val={user.age}
                    HandleInputEvent={HandleInput}
                    name="Age"
                    isValid={false}
                    validationName="You Must Select Age"
                />



                <Input HandleInputEvent={HandleInput}
                    val={user.phone}
                    placeholder="Phone"
                    type="tel"
                    name="phone"
                    isValid={false}
                    isValid2={false}
                    validationName="Field Must Have At List 10 Charters"
                    validationName2="Field Must Have Only Numbers"
                />

              

                <div className={props.onDeleteUser ? classes.btn_warp_delete : classes.btn_warp}>

                    <Button
                        butttoTitle={props.butttoTitle}
                        HandlePress={submitHandler}
                    />
                    {props.onDeleteUser ? <Button butttoTitle="Delete" HandlePress={handleDelete} /> : null}
                    <Button
                        butttoTitle="Back"
                        HandlePress={goToHome}
                    />

                </div>

            </form>
        </Card>

    )
}

export default UserForm
