import React, { useState, useEffect } from 'react'
import UseFetch from '../hooks/UseFetch';
import { useParams } from 'react-router-dom';
import UserForm from '../components/users/UserForm';
import { useNavigate } from 'react-router-dom';
import e from 'cors';


const EditUser = () => {
    var  userData ;
    const navigate = useNavigate();
    const { uid } = useParams();
    const [usersfromapi, setUsers] = useState([])
    const { loading, users } = UseFetch();
    const [isLoding, setIsLoading] = useState(true);
    const [userInitialData, setUserInitialData] = useState({});
    useEffect(() => {

        setUsers(users);

    }, [])

    useEffect(() => {

        if (!loading) {
            users.map(u => {
                if (u.id == uid) {
                  return   userData = {
                        first_name: u.first_name,
                        last_name: u.last_name,
                        age: u.age,
                        phone: u.phone
                    }
                    console.log(userData)
                   return setUserInitialData(userData);
                    
                }
            })
        }


    }, [])

    console.log(userData);

    if (loading) {
        return <section>
            <p>Loading...</p>
        </section>
    }



    return (
        <>
            <h1>Update User!!!</h1>
            {/* <h2>{singleuser.first_name} {singleuser.last_name}</h2> */}
            <UserForm userInitialData={userData} />
        </>
    )
}

