import React, { useState, useEffect } from 'react'
import UseFetch from '../hooks/UseFetch';
import { useParams } from 'react-router-dom';
import UserForm from '../components/users/UserForm';
import { useNavigate } from 'react-router-dom';


const EditUser = () => {
    var userInitialData;
    var  userData={} ;
    const navigate = useNavigate();
    const { uid } = useParams();
    const [loading, setLoading] = useState(true)
    const [users, setUsers] = useState([])
    //const [userInitialData, setUserInitialData] = useState({});





        const editUsser=(userdata)=>{
        const BASE_URL='http://localhost:8000/users/edit-user';
        console.log(userdata)
        fetch(BASE_URL,{
            method:'POST',
            body:JSON.stringify(userdata),
            headers:{
                'Content-Type':'application/json'
            }
        }).then(()=>{
            navigate('/')
        })

    }
    
    onSubmitUser={editUser}




    useEffect(() => {
        
    
        setLoading(true)
        const BASE_URL = 'http://localhost:8000/users'
        fetch(BASE_URL, { headers: { 
            'Content-Type': 'application/json',
            'Accept': 'application/json'
           }})
          .then(res => {
            return res.json()
          }).then(data => {
            const usersArr=[]
            for(const key in data){
              const user={         
                ...data[key]
              }
            
              
              usersArr.push(user)
            }
           
            setLoading(false)
            setUsers(usersArr)
        
      
          })
    
      }, [])
      

      if(users){
        users.map((u)=>{
            if(u.id==uid){
              return  userInitialData=u;
                
            }
        })
      }
      console.log(userInitialData)
      if(!userInitialData){
        return <div>Loading</div>
      }
        
        return (
            <>
                <h1>Update User!!!</h1>
              
                <UserForm userInitialData={userInitialData}  />
            </>
        )
    
 


 
}

export default EditUser






