import UseFetch from '../hooks/UseFetch';
import { useParams } from 'react-router-dom';
import UserForm from '../components/users/UserForm';
import {useNavigate} from 'react-router-dom';


const EditUser = () => {

    const navigate = useNavigate();
    const [usersfromapi, setUsers] = useState([]);
    const {loading , users} =UseFetch();
    const { id } = useParams();
    console.log(id)
    const  singleuser={};
   //const [initData,setInitData]=useState({});
    
    useEffect(() => {     
       
      setUsers(users);
     console.log(usersfromapi);
     users.find(u => u.id === id)
     console.log(singleuser)
    }, [])
    
  
    
   const initData={
        first_name:"",//singleuser.first_name,
        last_name:"",//singleuser.last_name,
        age:'',//singleuser.age,
        phone:"",//singleuser.phone
   };

    const waitToData=()=>{

        
    }


   



    if (loading) {
        return <section>
          <p>Loading...</p>
        </section>
      }else{
     
       // waitToData();
      }
    
    

      
      
      
      
    

    
   

    const editUser=(userdata)=>{
        const BASE_URL='http://localhost:8000/users/edit-user';
        console.log(userdata)
        fetch(BASE_URL,{
            method:'POST',
            body:JSON.stringify(userdata),
            headers:{
                'Content-Type':'application/json'
            }
        }).then(()=>{
            navigate('/')
        })

    }
   

    return (
        <>
            <h1>Update User!!!</h1>
            {/* <h2>{singleuser.first_name} {singleuser.last_name}</h2> */}
            <UserForm userInitialData={initData} onSubmitUser={editUser} />
        </>
    )
}

export default EditUser
