import React ,{ useState, useEffect } from 'react'
import UseFetch from '../hooks/UseFetch';
import { useParams } from 'react-router-dom';
import UserForm from '../components/users/UserForm';
import {useNavigate} from 'react-router-dom';


const EditUser = () => {

    const navigate = useNavigate();
    const [usersfromapi, setUsers] = useState([]);
    const {loading , users} =UseFetch();
    const { id } = useParams();

    const [singleUser,setSingelUser]=useState({});
    useEffect(() => {     
      setUsers(users);
     
    }, [])


 




    if (loading) {
        return <section>
          <p>Loading...</p>
        </section>
      }
      else{
          console.log(users)
          const singleuser = usersfromapi.find(u => u.id === id)
          console.log(singleuser)

       // setSingelUser(userInitialData);
      }
      
      const userInitialData={
        first_name:"",//singleuser.first_name,
        last_name:"",//singleuser.last_name,
        age:"",//singleuser.age,
        phone:""//singleuser.phone
    }
    

    
   

    const editUser=(userdata)=>{
        const BASE_URL='http://localhost:8000/users/edit-user';
        console.log(userdata)
        fetch(BASE_URL,{
            method:'POST',
            body:JSON.stringify(userdata),
            headers:{
                'Content-Type':'application/json'
            }
        }).then(()=>{
            navigate('/')
        })

    }
   

    return (
        <>
            <h1>Update User!!!</h1>
            {/* <h2>{singleuser.first_name} {singleuser.last_name}</h2> */}
            <UserForm userInitialData={userInitialData} onSubmitUser={editUser} />
        </>
    )
}

export default EditUser
