import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';

const UserForm = (props) => {


    const { uid } = useParams();
    const [firstName, setFirstName] = useState(props.userInitialData.first_name);
    const [lastName, setLasttName] = useState(props.userInitialData.last_name);
    const [age, setAge] = useState(props.userInitialData.age);
    const [phone, setPhone] = useState(props.userInitialData.phone);
    console.log(uid)
    console.log(props.userInitialData);
    if (props.userInitialData instanceof Array) {

        // const user = props.userInitialData.find(u => u.id === uid)
        props.userInitialData.map((u) => {
            if (u.id == uid) {
                //alert(u.first_name);
                setFirstName(u.first_name);
                setLasttName(u.last_name);
                setAge(u.age);
                setPhone(u.phone);
                return u;
            }
        })
        // console.log(user)
        // setFirstName(user.first_name);
        // setLasttName(user.last_name);
        // setAge(user.age);
        // setPhone(user.phone);
    }
    const navigate = useNavigate();
    const [options, setOptions] = useState([]);
    useEffect(() => {
        let arr = [];
        for (var i = 0; i < 121; i++) {
            arr.push(i)
        }
        setOptions(arr)

    }, [])




    const submitHandler = (event) => {
        event.preventDefault();
        const user = {
            first_name: firstName,
            last_name: lastName,
            age: age,
            phone: phone
        }

        props.onSubmitUser(user);

    }
    const handlePhone = (e) => {
        setPhone(e.target.value);

    }

    const handleFirstName = (e) => {
        setFirstName(e.target.value)

    }
    const handleLastName = (e) => {

        setLasttName(e.target.value)

    }

    const handleAge = (e) => {
        setAge(e.target.value);


    }

    const goToHome = () => {
        navigate('/');
    }

    return (

        <form onSubmit={submitHandler}>



            <input type="text" required id="first-name"
                onChange={handleFirstName} value={firstName} value={firstName} />


            <input type="text" required id="last-name"
                onChange={handleLastName} value={lastName} value={lastName} />



            <select
                value={age}
                onChange={handleAge}
            >
                {
                    options.map((i) => {
                        return <option key={i} value={i}>{i}</option>
                    })
                }

            </select>

            <input type="tel" id="phone" name="phone" required
                onChange={handlePhone}
                value={phone}
            />


            <button>Add User</button>
            <button onClick={goToHome}>Back</button>


        </form>


    )
}

export default UserForm
