import styled from 'styled-components';



export const InputStyle = styled.input`
border: 1px solid lightgray;
height: 35px;
width: 200px;
margin: 14px;
outline: none;
color: #a9a9a9 ;
&::{placeholder:color: #a9a9a9;}
`

export const SelectStyle = styled.select`
border: 1px solid lightgray;
height: 35px;
width: 205px;   
outline: none;
margin: 14px;
color: #a9a9a9 ;

`
