import React, { useState,  } from 'react'
import UseFetch from '../hooks/UseFetch';
import { useParams } from 'react-router-dom';
import UserForm from '../components/users/UserForm';
import { useNavigate } from 'react-router-dom';


const EditUser = () => {
    const [icon,setIcon]=useState();
    var userInitialData;
    var userData = {};
    const navigate = useNavigate();
    const { uid } = useParams();







    const editUser = (userdata) => {
        console.log(userData)
        userData.id = uid;
        const BASE_URL = 'http://localhost:8000/users/edit-user';
        console.log(userdata)
        fetch(BASE_URL, {
            method: 'POST',
            body: JSON.stringify(userdata),
            headers: {
                'Content-Type': 'application/json'
            }
        }).then(() => {
            navigate('/')
        })

    }

    const { loading, users } = UseFetch();


    if (loading) {
        return <section>
            <p>Loading...</p>
        </section>
    }




    if (users) {
        users.map((u) => {
            if (u.id === uid) {
                return userInitialData = u;

            }
        })
    }

    if (!userInitialData) {
        return <div>Loading</div>
    }

    return (
        <>
            <h1>Update User!!!</h1>
            <h1>{userInitialData.first_name} {userInitialData.last_name} </h1>
            <UserForm userInitialData={userInitialData} onSubmitUser={editUser} butttoTitle="Edit User"/>
        </>
    )





}

export default EditUser
