import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import Card from '../ui/Card'
import classes from './userform.module.css'
const UserForm = (props) => {


    const { uid } = useParams();
    const navigate = useNavigate();


    const [firstName, setFirstName] = useState(props.userInitialData.first_name);
    const [lastName, setLastName] = useState(props.userInitialData.last_name);
    const [age, setAge] = useState(props.userInitialData.age);
    const [phone, setPhone] = useState(props.userInitialData.phone);

    const [validation, setValidation] = useState(
        {
            isOnChangEventFirstName: false,
            isOnChangEventLastName: false,
            isOnChangEventPhone: false,
        }
    );


    const [options, setOptions] = useState([]);



    useEffect(() => {
        let arr = [];
        for (var i = 0; i < 121; i++) {
            arr.push(i)
        }
        setOptions(arr)

    }, [])

    const handleDelete = (event) => {
        event.preventDefault();

        if (uid) {

            props.onDeleteUser(uid);
        }

    }


    const submitHandler = (event) => {
        event.preventDefault();
        console.log('submit handler');

        if (phone.length < 9 || isNaN(phone) || firstName.length < 3 || lastName.length < 3) {

            setValidation({
                isOnChangEventFirstName: true,
                isOnChangEventLastName: true,
                isOnChangEventPhone: true,
            });
            return;
        }

        const user = {
            first_name: firstName,
            last_name: lastName,
            age: age,
            phone: phone
        }
        if (uid) {
            user.id = uid;
        }

        props.onSubmitUser(user);

    }

    const handlePhone = (e) => {
        e.preventDefault();
        setValidation({ ...false, isOnChangEventPhone: true });
        setPhone(e.target.value);

    }

    const handleFirstName = (e) => {
        e.preventDefault();
        setValidation({ ...false, isOnChangEventFirstName: true });
        setFirstName(e.target.value)

    }
    const handleLastName = (e) => {
        e.preventDefault();
        setLastName(e.target.value)
        setValidation({ ...false, isOnChangEventLastName: true });
        //console.log(validation);
    }

    const handleAge = (e) => {
        e.preventDefault();
        setAge(e.target.value);


    }

    const goToHome = () => {
        navigate('/');
    }


    return (
        <Card>
            <form onSubmit={submitHandler} className={classes.form}>



                <input type="text" required
                    className={classes.input}
                    onChange={handleFirstName}
                    value={firstName}
                    placeholder="First Name"

                />

                {validation.isOnChangEventFirstName && firstName.length < 3 ? <div>Filed must have at list 3  charters</div> : ''}


                <input type="text" required
                    className={classes.input}
                    onChange={handleLastName} value={lastName}
                    placeholder="Last Name"

                />

                {validation.isOnChangEventLastName && lastName.length < 3 ? <div>Filed must have at list 3  charters</div> : ''}


                <select
                    value={age}
                    onChange={handleAge}
                    className={classes.select}

                >
                    <option value="Age" disabled selected hidden>Age</option>
                    {
                        options.map((i) => {

                            return <option key={i} value={i}>{i}</option>
                        })
                    }

                </select>


                <input type="tel" name="phone" required
                    className={classes.input}
                    onChange={handlePhone}
                    value={phone}
                    placeholder="Phone"
                />

                <br />
                {validation.isOnChangEventPhone && phone.length < 10 ? <div>Filed must have at list 10  charters</div> : ''}
                {validation.isOnChangEventPhone && isNaN(phone) ? <div>Filed must have only numbers</div> : ''}

                <div>
                    <button>{props.butttoTitle}</button>
                    {props.onDeleteUser ? <button onClick={handleDelete}>Delete User</button> : null}
                    <button onClick={goToHome}>Back</button>
                </div>

            </form>
        </Card>

    )
}

export default UserForm
