import React, { useState,useEffect } from 'react'


const UserForm = () => {

    const [firstName, setFirstName] = useState();
    const [lastName, setLasttName] = useState();
    const [age, setAge] = useState();
    const [phone, setPhone] = useState();

    const [options, setOption] = useState([]);

    const submitHandler = (event) => {


    }
    const handlePhone=(e)=>{
        setPhone(e.target.value);
    }
    
    const handleFirstName=(e)=>{
        setFirstName(e.target.value)
    }
    const handleLastName=(e)=>{
        setLasttName(e.target.value)
    }


    useEffect(()=>{
        const arr=[];
        for(let i=0;i<120;i++){
            arr.push(i)
        }
        setOption(arr);
    },[])



    return (

        <form onSubmit={submitHandler}>



            <input type="text" required id="first-name"
                onChange={handleFirstName} value={firstName} value={firstName} />


            <input type="text" required id="last-name"
                onChange={handleLastName} value={lastName} value={lastName} />


          
            <select
                value={age}
                onChange={(e)=>setAge(e.target.value)}
            >
                {
                    options.map(i=>{
                        <options value={i}>{i}</options>
                    })
                }
            </select>

            <input type="tel" id="phone" name="phone" pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}" required
                onChange={handlePhone}
                value={phone}
            />


            <button>Add User</button>
            <button>Back</button>


        </form>


    )
}

export default UserForm
