import React, { useState, useEffect } from 'react'


const UserForm = () => {

    var  ageArr=[];
    const [options, setOptions] = useState();
    useEffect(() => {
        ageArr=populateArr();
        
    }, [])
    console.log(ageArr)
    const [firstName, setFirstName] = useState();
    const [lastName, setLasttName] = useState();
    const [age, setAge] = useState();
    const [phone, setPhone] = useState();

    const [phone1, setPhone1] = useState(120);
    const populateArr=()=>{        
        let arr = [];    
        for (var i = 0; i < 121; i++) {
            arr.push(i)
            //console.log(options)
        }
        return arr;
    }
    
    const submitHandler = (event) => {


    }
    const handlePhone = (e) => {
        setPhone(e.target.value);
    }

    const handleFirstName = (e) => {
        setFirstName(e.target.value)
    }
    const handleLastName = (e) => {
        setLasttName(e.target.value)
    }

    



    return (

        <form onSubmit={submitHandler}>



            <input type="text" required id="first-name"
                onChange={handleFirstName} value={firstName} value={firstName} />


            <input type="text" required id="last-name"
                onChange={handleLastName} value={lastName} value={lastName} />



            <select
                value={age}
                onChange={(e) => setAge(e.target.value)}
            >
                {}

            </select>

            <input type="tel" id="phone" name="phone" pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}" required
                onChange={handlePhone}
                value={phone}
            />


            <button>Add User</button>
            <button>Back</button>


        </form>


    )
}

export default UserForm
