import React ,{ useState, useEffect } from 'react'
import UseFetch from '../hooks/UseFetch';
import { useParams } from 'react-router-dom';
import UserForm from '../components/users/UserForm';
import {useNavigate} from 'react-router-dom';


const EditUser = () => {
   
    const navigate = useNavigate();
    const { uid } = useParams();
    const [usersfromapi, setUsers] = useState([])
    const {loading , users} =UseFetch();
  
    useEffect(() => {
      
      setUsers(users);
      
    }, [])
   
    if (loading) {
      return <section>
        <p>Loading...</p>
      </section>
    }

      
    console.log(users)
      
    const find_user=users.find((u)=>u.id===uid);
    

    console.log(find_user)
    if(find_user){
        var  userInitialData={
            first_name:find_user.first_name,
            last_name:find_user.last_name,
            age:find_user.age,
            phone:find_user.phone
        }
    }else{
        var  userInitialData={
            first_name:"",
            last_name:"",
            age:"",
            phone:""
        }
    }
   
    // const editUser=(userdata)=>{
    //     const BASE_URL='http://localhost:8000/users/edit-user';
    //     console.log(userdata)
    //     fetch(BASE_URL,{
    //         method:'POST',
    //         body:JSON.stringify(userdata),
    //         headers:{
    //             'Content-Type':'application/json'
    //         }
    //     }).then(()=>{
    //         navigate('/')
    //     })

    // }
   //
//onSubmitUser={editUser}
    return (
        <>
            <h1>Update User!!!</h1>
            {/* <h2>{singleuser.first_name} {singleuser.last_name}</h2> */}
            <UserForm  userInitialData={userInitialData} />
        </>
    )
}

export default EditUser
