import React, { useEffect,useState } from 'react'
import Vlidation from './Validation';





//need to solve styling 
const Select = (props) => {


    const selectHandler=(e)=>{

        props.HandleInputEvent(e,props.name);
    } 


    //let is limited only to his scope let wont work in this case.
    var arr=[];
    const [options, setOptions] = useState(new Array(121));

    useEffect(() => {
        
        
        
        arr = Array.apply(null, { length: 121 }).map((elem, idx)=> {
            return idx + 1;
        });

        console.log(arr)


    
        setOptions(arr)
        console.log("options is ",options)
    }, [])



    return (
        <>
        <select
            value={props.val}
            onChange={selectHandler}
        // className={validation.isOnChangEventAge && age == 'Age' ? classes.red : classes.select}

        >
            <option defaultValue="Age">Age</option>
            {
                options.map((value) => {
                    return <option key={value} value={props.value}>{value}</option>
                })

            }

        </select>

        {!props.isValid? <Vlidation validationName={props.validationName} /> :null}
        </>
    )
}

export default Select
