import React from 'react'
import Vlidation from './Validation';



const Input = (props) => {

    const inputHandler = (e) => {

        props.HandleInputEvent(e, props.name);
    }
    
    ///handleVliation here
    

    return (
        <>
            <input type={props.type} required
                onChange={inputHandler}
                value={props.val}
                placeholder={props.placeholder}

            />
            
            {!props.isValid? <Vlidation validationName={props.validationName} /> :null}
            {props.validationName2&&!props.isValid2? <Vlidation validationName={props.validationName} /> :null}
            
        </>
    )
}

export default Input
