import styled from 'styled-components';


export const ButtonAddOrEditStyle = styled.button`
    width: 600px;
    cursor: pointer;
    outline: none;
    border-radius: 4px;
    outline: none;
    border: none;
    height: 25px;
    color:#fff;
    background-color:${(props) => props.backColor};
    font-weight: 600;
`;
export const BtnWrap = styled.div  `
        width:55%;
        padding: 12px;
        display: flex;
        justify-content: space-between;
        margin-top: 10px;
        margin:20px;

`
